UserQryDef.QuerySQL:
DECLARE @fname varchar(20), @lname varchar(20);
SET @fname = ISNULL('@#firstName#@', ''); SET @lname = ISNULL('@#lastName#@', '');
SELECT ohd.inactive as [AcctStatus] ,ohd.accountno as [AccountNo] ,b.inactive as [BadgeStatus] ,b.badgeno as [BadgeNo] ,ohd.firstname as [FirstName] ,ohd.lastname as [LastName]
FROM tblAccountOHD as ohd left join tblBadgesOHD as b ON ohd.accountno = b.accountno
WHERE ohd.firstname LIKE @fname + '%' and ohd.lastname LIKE '%' + @lname + '%'
GROUP BY ohd.accountno, b.badgeno, ohd.firstname, ohd.lastname, ohd.inactive, b.inactive ORDER BY ohd.accountno, b.badgeno, ohd.lastname, ohd.firstname
